import React, { useState } from "react";
import "./style.css";
import { useStoreContext } from "../../utils/GlobalState";

function SignUpForm() {
  const [state, dispatch] = useStoreContext();
  const [formInput, setFormInput] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
  });

  const [error, setError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const hanldeFormInput = (event) => {
    const { name, value } = event.target;
    setFormInput({ ...formInput, [name]: value });
  };

  const HandleSignUpBtn = async (e) => {
    e.preventDefault();
    if (
      formInput.email.length > 1 &&
      formInput.password.length >= 8 &&
      formInput.firstName.length > 1 &&
      formInput.lastName.length > 1
    ) {
      API.signUp({
        first_name: formInput.first_name,
        last_name: formInput.last_name,
        email: formInput.email,
        password: formInput.password,
      });
      const response = await fetch("/api/users/signup", {
        method: "POST",
        body: JSON.stringify({
          firstName,
          lastName,
          email,
          password,
        }),
        headers: { "Content-Type": "application/json" },
      });
      if (response.ok) {
        alert(
          "you have successfully signed up! Login to use all the cool features!"
        );
        hanldeSignUpFinish();
      } else {
        alert(response.statusText);
      }
    }
  };
  // This is not returning the state back to true
  const hanldeSignUpFinish = () => {
    // e.preventDefault();
    console.log("hey");
    dispatch({
      type: "LOGIN",
      login: true,
    });
  };

  return (
    <div className="col-md-4 signupContainer">
      <form className="form signup-form signup">
        <div className="form-group">
          <label>First Name:</label>
          <input
            className="form-input"
            type="name"
            id="firstname-signup"
            name="first_name"
            onChange={hanldeFormInput}
          />
        </div>
        <div className="form-group">
          <label> Last Name:</label>
          <input
            className="form-input"
            type="name"
            id="lastname-signup"
            name="last_name"
            onChange={hanldeFormInput}
          />
        </div>
        <div className="form-group">
          <label>email:</label>
          <input
            className="form-input"
            type="text"
            id="email-signup"
            name="email"
            onChange={hanldeFormInput}
          />
        </div>
        <div className="form-group">
          <label>password:</label>
          <input
            className="form-input"
            type="password"
            id="password-signup"
            name="password"
            onChange={hanldeFormInput}
          />
        </div>
        <div className="form-group">
          <button
            id="signup"
            className="btn btn-primary"
            onClick={HandleSignUpBtn}
          >
            signup
          </button>
          <button onClick={hanldeSignUpFinish}>Back to Login</button>
        </div>
      </form>
    </div>
  );
}

export default SignUpForm;
