import React from "react";
import { useStoreContext } from "../../utils/GlobalState";
import { Link } from "react-router-dom";
import PendingEvent from "../Event/PendingEvent";
import "./style.css";

function Pending() {
  const [state, dispatch] = useStoreContext();

  const EventSelected = (event) => {
    const eventId = event.target.id;
    const selectedEvent = state.pendingEvents.filter(
      (event) => event.id === eventId
    );
    dispatch({
      type: "CURRENTBOOKING",
      currentBooking: getEvent[0],
    });
  };

  return (
    <div>
      <div className="container">
        <div>
          <Link to="/" className="backBtnW">
            Click me to go home
          </Link>
        </div>
        <div className="row">
          <div className="col-md-5">
            <div className="eventList">
              <div className="listContainer">
                <h2 className="h2">Pending Events</h2>
                <ul className="ul">
                  {state.pendingEvents.map((event) => {
                    return (
                      <li className="li events eventTitle" key={event.id}>
                        <Link
                          to="#"
                          scope="row"
                          id={event.id}
                          onClick={EventSelected}
                          key={event.id + "link"}
                        >
                          {event.title}
                        </Link>
                      </li>
                    );
                  })}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
      {state.clickedEvent ? (
        <PendingEvent />
      ) : (
        <div className="noEventClicked col-md-7">
          Click an event to view it!
        </div>
      )}
    </div>
  );
}

export default Pending;
