import React, { useState, useEffect } from "react";
import API from "../../utils/API";
import { useParams, Link } from "react-router-dom";
import "./style.css";
import { useHistory } from "react-router-dom";

function Day() {
  const [getBookings, setBookings] = useState([""]);
  const [noEvents, setNoEvents] = useState(false);
  const [clickedEvent, setClickedEvent] = useState(false);
  const [currentBooking, setCurrentBooking] = useState("");

  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    API.getBookings(id).then((res) => {
      if (res.data.length > 0) {
        setNoEvents(true);
      }
      setBookings(res.data);
      console.log(res.data);
    });
  }, [noEvents]);

  // const clickTitle = () => {
  //   dispatch({
  //     type: "DATECLICKED",
  //     // type: "",
  //   });
  // };
  const clickEvent = (event) => {
    const eventId = event.target.id;
    const getEvent = getBookings.filter((event) => event.id == eventId);
    console.log(getEvent);
    setClickedEvent(!clickedEvent);
  };

  return (
    <div>
      {noEvents ? (
        <div className="container">
          <div className="row">
            <div className="col-md-5">
              <div className="eventList">
                {getBookings.map((booked) => {
                  return (
                    <div
                      className="eventTitle"
                      key={booked.id}
                      id="{booked.id}"
                    >
                      {" "}
                      {/* <Link to={`/date/booking/${booked.id}`}> */}
                      <p scope="row" onClick={clickEvent} id={booked.id}>
                        {booked.title}
                      </p>
                      {/* </Link> */}
                    </div>
                  );
                })}
              </div>
            </div>
            {clickedEvent ? (
              <div className="eventList col-md-7">
                <div className="list-group">
                  <a
                    href="#"
                    className="list-group-item list-group-item-action flex-column align-items-start active"
                  >
                    <div className="d-flex w-100 justify-content-between">
                      <h5 className="mb-1">List group item heading</h5>
                      <small>3 days ago</small>
                    </div>
                    <p className="mb-1">
                      Donec id elit non mi porta gravida at eget metus. Maecenas
                      sed diam eget risus varius blandit.
                    </p>
                    <small>Donec id elit non mi porta.</small>
                  </a>
                  <a
                    href="#"
                    className="list-group-item list-group-item-action flex-column align-items-start"
                  >
                    <div className="d-flex w-100 justify-content-between">
                      <h5 className="mb-1">List group item heading</h5>
                      <small className="text-muted">3 days ago</small>
                    </div>
                    <p className="mb-1">
                      Donec id elit non mi porta gravida at eget metus. Maecenas
                      sed diam eget risus varius blandit.
                    </p>
                    <small className="text-muted">
                      Donec id elit non mi porta.
                    </small>
                  </a>
                </div>
              </div>
            ) : (
              <div className="noEventClicked col-md-7">event clicked</div>
            )}
          </div>
        </div>
      ) : (
        <h1>You have nothing but time today!</h1>
      )}
    </div>
  );
}

export default Day;

