import React from "react";
import FeatherIcon from "feather-icons-react";
import { Link } from "react-router-dom";
import API from "../../utils/API";

function Event(props) {
  const deleteEvent = () => {
    if (window.confirm("Delete this Event?")) {
      API.deletePost(props.currentBooking[0].id)
        .then((res) => {
          console.log("Ben changhe this to re mount the other component maybe");
        })
        .catch((err) => console.log(err));
    }
  };

  return (
    <div
      className="clickedEvent col-md-6"
      onClick={console.log(props.bookingWith)}
    >
      {props.deleteAnEvent ? (
        <div class="alert alert-dismissible alert-warning">
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="alert"
          ></button>
          <h4 class="alert-heading">Warning!</h4>
          <p class="mb-0">
            Best check yo self, you're not looking too good. Nulla vitae elit
            libero, a pharetra augue. Praesent commodo cursus magna,{" "}
            <a href="#" class="alert-link">
              vel scelerisque nisl consectetur et
            </a>
            .
          </p>
        </div>
      ) : (
        <div className="hide"></div>
      )}
      <div className="listCOntainer">
        <div className="updatePosts">
          <FeatherIcon
            icon="edit"
            className="updatePost"
            // onClick={updatEvent}
          />
          <FeatherIcon
            icon="trash-2"
            className="updatePost"
            onClick={deleteEvent}
          />
        </div>
        <h2 className="h2">
          {" "}
          {props.currentBooking[0].title} ~ {props.bookingWith}
        </h2>
        <ul className="ul">
          <li className="li">
            <Link to="#"> {props.currentBooking[0].description}</Link>
          </li>
          <li className="li">
            <Link to="#"> {props.currentBooking[0].location}</Link>
          </li>
          <li className="li">
            <Link to="#"> {props.currentBooking[0].time}</Link>
          </li>
          <li className="li">
            <Link to="#"> {props.currentBooking[0].date}</Link>
          </li>
          <li className="li"></li>
        </ul>
      </div>
    </div>
  );
}

export default Event;
