import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import API from "../../utils/API";
import { useParams } from "react-router-dom";
import Timepicker from "../TimePicker";

function AltUserCalendar() {
  const { id } = useParams();

  const [modal, setModal] = useState(false);
  const [user, setUser] = useState({});
  const [modalStyle, setModalStyle] = useState({
    class: "modal",
    style: "none",
  });
  const [dateClicked, setDateClicked] = useState("");
  const [eventInput, setEventInput] = useState({
    title: "",
    description: "",
    time: "",
    duration: "",
    location: "",
  });

  useEffect(() => {
    API.getUser(id).then((res) => {
      console.log(res.data);
      setUser(res.data);
    });
  }, []);

  const handleDateClick = (arg) => {
    setModalStyle({
      class: "modal fade show",
      style: "block",
    });
    setDateClicked(arg.dateStr);
  };

  const handleExitClick = (e) => {
    e.preventDefault();
    setModalStyle({
      class: "modal",
      style: "none",
    });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEventInput({ ...eventInput, [name]: value });
  };

  const handleEventSubmit = (e) => {
    e.preventDefault();
    console.log(eventInput);
  };

  return (
    <div>
      <h1>
        Welcome to {user.first_name}'s Calendar! Cick a day to add en event!
      </h1>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      <div
        className={modalStyle.class}
        id="source-modal"
        style={{ display: modalStyle.style }}
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Add an event for {dateClicked}</h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
                onClick={handleExitClick}
              >
                {/* <span aria-hidden="true"></span> */}
              </button>
            </div>
            <div className="modal-body">
              <label className="col-form-label mt-4">Title</label>
              <input
                type="text"
                className="form-control"
                placeholder="Title your event"
                id="inputTitle"
                name="title"
                onChange={handleInputChange}
              ></input>
              <label className="col-form-label mt-4">Description</label>
              <input
                type="text"
                className="form-control"
                placeholder="Provide a description"
                id="inputDescription"
                name="description"
                onChange={handleInputChange}
              ></input>
              <label className="col-form-label mt-4">Time</label>
              <input
                type="text"
                className="form-control"
                placeholder="e.g. 2pm-3pm"
                id="inputTime"
                name="time"
                onChange={handleInputChange}
              />

              <label className="col-form-label mt-4">Location</label>
              <input
                type="text"
                className="form-control"
                placeholder="e.g. Adelaide"
                id="inputLocation"
                name="location"
                onChange={handleInputChange}
              ></input>
              <div class="alert alert-dismissible alert-danger">
                <button
                  type="button"
                  class="btn-close"
                  data-bs-dismiss="alert"
                ></button>
                <strong>Oh snap!</strong>{" "}
                <a href="#" class="alert-link">
                  Change a few things up
                </a>{" "}
                and try submitting again.
              </div>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleEventSubmit}
              >
                Save Event
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
                onClick={handleExitClick}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AltUserCalendar;
