import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import FeatherIcon from "feather-icons-react";
import { useStoreContext } from "../../utils/GlobalState";
import API from "../../utils/API";

function Notifications() {
  const [state, dispatch] = useStoreContext();

  useEffect(() => {
    API.getPending()
      .then((res) => {
        if (res.data.length === 0) {
          return;
        }
        dispatch({
          type: "PENDINGEVENT",
          pendingEvents: res.data,
        });
      })
      .catch((err) => console.log(err));
  }, []);

  const poo = () => {
    console.log(state.pendingEvents);
  };

  return (
    <div onClick={poo}>
      <Link
        to="#"
        className="notification nav-link dropdown-toggle"
        data-bs-toggle="dropdown"
        role="button"
        aria-haspopup="true"
        aria-expanded="false"
      >
        <span>
          <FeatherIcon icon="bell" />
        </span>
        <span className="badge">{state.pendingEvents.length}</span>
      </Link>
      <div className="dropdown-menu">
        <Link className="dropdown-item" to="#">
          <h5> Your Pending Events: </h5>
        </Link>
        {state.pendingEvents.map((event) => {
          return (
            <Link className="dropdown-item" to="#" key={event.id}>
              {event.title}
            </Link>
          );
        })}
      </div>
    </div>
  );
}

export default Notifications;
