import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import API from "../../utils/API";
import { useParams } from "react-router-dom";

function AltUserCalendar() {
  const { id } = useParams();

  const [modal, setModal] = useState("modal");

  useEffect(() => {
    API.getUser(id).then((res) => {
      console.log(res.data);
    });
  }, []);

  const handleDateClick = (arg) => {
    // API.getUser(id).then((res) => {
    //   console.log(res.data);
    // });
    document.querySelector("#source-modal").classList.add("fade show");
  };
  return (
    <div>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      <div className="modal" id="source-modal">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Modal title</h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true"></span>
              </button>
            </div>
            <div className="modal-body">
              <label className="col-form-label mt-4">Default input</label>
              <input
                type="text"
                className="form-control"
                placeholder="Default input"
                id="inputDefault"
              ></input>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-primary">
                Save changes
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AltUserCalendar;
