import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import API from "../../utils/API";
import { useParams } from "react-router-dom";
import EventInput from "../eventInput";
import { useStoreContext } from "../../utils/GlobalState";

function AltUserCalendar() {
  const { id } = useParams();

  const [modal, setModal] = useStoreContext;
  const [user, setUser] = useState({});

  const [dateClicked, setDateClicked] = useState("");

  useEffect(() => {
    API.getUser(id).then((res) => {
      console.log(res.data);
      setUser(res.data);
    });
  }, []);

  const handleDateClick = (arg) => {
    setDateClicked(arg.dateStr);
    setModal(true);
  };

  return (
    <div>
      <h1>
        Welcome to {user.first_name}'s Calendar! Cick a day to add en event!
      </h1>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      {modal ? (
        <EventInput props={(dateClicked, modal, setModal)} />
      ) : (
        <div></div>
      )}
    </div>
  );
}

export default AltUserCalendar;
