import React, { useState, useEffect } from "react";
import API from "../../utils/API";
import { useParams, Link } from "react-router-dom";
import "./style.css";
import { useStoreContext } from "../../utils/GlobalState";
import FeatherIcon from "feather-icons-react";

function Day() {
  const [getBookings, setBookings] = useState([""]);
  const [noEvents, setNoEvents] = useState(false);
  const [clickedEvent, setClickedEvent] = useState(false);
  const [currentBooking, setCurrentBooking] = useState({});
  const [bookingWith, setBookingWith] = useState();
  const [deleteAnEvent, setDeleteAnEvent] = useState(false);

  const { id } = useParams();
  const [state, dispatch] = useStoreContext();

  useEffect(() => {
    API.getBookings(id, state.user_id).then((res) => {
      if (res.data.length > 0) {
        setNoEvents(true);
      }
      setBookings(res.data);
      console.log(res.data);
    });
  }, [noEvents]);

  const clickEvent = (event) => {
    const eventId = event.target.id;
    const getEvent = getBookings.filter((event) => event.id == eventId);
    setCurrentBooking(getEvent);
    console.log("created by user id: " + getEvent[0].createdByUser.id);
    console.log("received by user id: " + getEvent[0].receivedByUser.id);

    if (state.user_id === getEvent[0].receivedByUser.id) {
      setBookingWith(
        getEvent[0].createdByUser.first_name +
          " " +
          getEvent[0].createdByUser.last_name
      );
    }
    if (state.user_id === getEvent[0].createdByUser.id) {
      setBookingWith(
        getEvent[0].receivedByUser.first_name +
          " " +
          getEvent[0].receivedByUser.last_name
      );
    } else console.log(bookingWith);
    setClickedEvent(true);
  };

  const deleteEvent = () => {
    if (confirm("Delete this Event?")) {
      API.deletePost(currentBooking[0].id)
        .then((res) => {
          API.getBookings(id, state.user_id).then((res) => {
            if (res.data.length > 0) {
              setNoEvents(true);
            }
            setBookings(res.data);
          });
        })
        .catch((err) => console.log(err));
    }
  };

  return (
    <div>
      {noEvents ? (
        <div className="container">
          <div>
            <Link to="/" className="backBtnW">
              Click me to go back
            </Link>
          </div>
          <div className="row">
            <div className="col-md-5">
              <div className="eventList">
                <div className="listContainer">
                  <h2 className="h2"> Today's Events</h2>
                  <ul className="ul">
                    {" "}
                    {getBookings.map((booked) => {
                      return (
                        <li className="li events eventTitle" key={booked.id}>
                          {booked.booker_id === state.user_id ? (
                            <Link
                              to="#"
                              scope="row"
                              onClick={clickEvent}
                              id={booked.id}
                              style={{ color: "red" }}
                              key={booked.id + "link"}
                            >
                              {booked.title}
                            </Link>
                          ) : (
                            <Link
                              to="#"
                              scope="row"
                              onClick={clickEvent}
                              id={booked.id}
                              style={{ color: "blue" }}
                              key={booked.id + "link"}
                            >
                              {booked.title}
                            </Link>
                          )}
                        </li>
                      );
                    })}
                  </ul>
                </div>
              </div>
            </div>
            {clickedEvent ? (
              <div className="clickedEvent col-md-6">
                {deleteAnEvent ? (
                  <div class="alert alert-dismissible alert-warning">
                    <button
                      type="button"
                      class="btn-close"
                      data-bs-dismiss="alert"
                    ></button>
                    <h4 class="alert-heading">Warning!</h4>
                    <p class="mb-0">
                      Best check yo self, you're not looking too good. Nulla
                      vitae elit libero, a pharetra augue. Praesent commodo
                      cursus magna,{" "}
                      <a href="#" class="alert-link">
                        vel scelerisque nisl consectetur et
                      </a>
                      .
                    </p>
                  </div>
                ) : (
                  <div className="hide"></div>
                )}
                <div className="listCOntainer">
                  <div className="updatePosts">
                    <FeatherIcon
                      icon="edit"
                      className="updatePost"
                      // onClick={updatEvent}
                    />
                    <FeatherIcon
                      icon="trash-2"
                      className="updatePost"
                      onClick={deleteEvent}
                    />
                  </div>
                  <h2 className="h2">
                    {" "}
                    {currentBooking[0].title} ~ {bookingWith}
                  </h2>
                  <ul className="ul">
                    <li className="li">
                      <Link to="#"> {currentBooking[0].description}</Link>
                    </li>
                    <li className="li">
                      <Link to="#"> {currentBooking[0].location}</Link>
                    </li>
                    <li className="li">
                      <Link to="#"> {currentBooking[0].time}</Link>
                    </li>
                    <li className="li">
                      <Link to="#"> {currentBooking[0].date}</Link>
                    </li>
                    <li className="li"></li>
                  </ul>
                </div>
              </div>
            ) : (
              <div className="noEventClicked col-md-7">
                Click an event to view it!
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="centre">
          <h1>
            You have nothing but time today!{" "}
            <div>
              <Link to="/" className="backBtn">
                Click me to go back
              </Link>
            </div>
          </h1>
        </div>
      )}
    </div>
  );
}

export default Day;
