import React, { useState, useEffect } from "react";
import API from "../../utils/API";
import { useParams, Link } from "react-router-dom";
import "./style.css";

function Day() {
  const [currentBooking, setCurrentBooking] = useState([""]);
  const [noEvents, setNoEvents] = useState(false);
  const [clickedEvent, setClickedEvent] = useState(false);

  const { id } = useParams();

  useEffect(() => {
    API.getBookings(id).then((res) => {
      if (res.data.length > 0) {
        setNoEvents(true);
      }
      setCurrentBooking(res.data);
      console.log(res.data);
    });
  }, [noEvents]);

  // const clickTitle = () => {
  //   dispatch({
  //     type: "DATECLICKED",
  //     // type: "",
  //   });
  // };
  const clickEvent = () => {
    setClickedEvent(!clickedEvent);
  };

  return (
    <div>
      {noEvents ? (
        <div className="container">
          <div className="row">
            <div className="col-md-5">
              <div className="eventList">
                {currentBooking.map((booked) => {
                  return (
                    <div className="eventTitle" key={booked.id}>
                      {" "}
                      {/* <Link to={`/date/booking/${booked.id}`}> */}
                      <th scope="row" onClick={clickEvent}>
                        {booked.title}
                      </th>
                      {/* </Link> */}
                    </div>
                  );
                })}
              </div>
            </div>
            {clickedEvent ? (
              <div className="noEventClicked col-md-7">
                Click an event to view it
              </div>
            ) : (
              <div className="noEventClicked col-md-7">event clicked</div>
            )}
          </div>
        </div>
      ) : (
        <h1>You have nothing but time today!</h1>
      )}
    </div>
  );
}

export default Day;

