import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import API from "../../utils/API";
import { useParams } from "react-router-dom";

function AltUserCalendar() {
  const { id } = useParams();

  const [modal, setModal] = useState(false);
  const [user, setUser] = useState({});
  const [modalStyle, setModalStyle] = useState({
    class: "modal",
    style: "none",
  });
  const [dateClicked, setDateClicked] = useState("");
  const [eventInput, setEventInput] = useState({
    title: "",
    description: "",
    startTime: {},
    endTime: {},
    duration: "",
    location: "",
  });

  useEffect(() => {
    API.getUser(id).then((res) => {
      console.log(res.data);
      setUser(res.data);
    });
  }, []);

  const handleDateClick = (arg) => {
    setModalStyle({
      class: "modal fade show",
      style: "block",
    });
    setDateClicked(arg.dateStr);
  };

  const handleExitClick = (e) => {
    e.preventDefault();
    setModalStyle({
      class: "modal",
      style: "none",
    });
  };

  const handleInputChange = () => {};

  return (
    <div>
      <h1>
        Welcome to {user.first_name}'s Calendar! Cick a day to add en event!
      </h1>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      <div
        className={modalStyle.class}
        id="source-modal"
        style={{ display: modalStyle.style }}
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Add an event for {dateClicked}</h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
                onClick={handleExitClick}
              >
                {/* <span aria-hidden="true"></span> */}
              </button>
            </div>
            <div className="modal-body">
              <label className="col-form-label mt-4">Title</label>
              <input
                type="text"
                className="form-control"
                placeholder="Title your event"
                id="inputTitle"
              ></input>
              <label className="col-form-label mt-4">Description</label>
              <input
                type="text"
                className="form-control"
                placeholder="Provide a description"
                id="inputDescription"
              ></input>
              <label className="col-form-label mt-4">Time</label>
              <input
                type="text"
                className="form-control"
                placeholder="e.g. 2pm-3pm"
                id="inputTime"
              ></input>
              <div class="container">
                <div class="row">
                  <div class="col-sm-6">
                    <div class="form-group">
                      <div class="input-group date" id="datetimepicker3">
                        <input type="text" class="form-control" />
                        <span class="input-group-addon">
                          <span class="glyphicon glyphicon-time"></span>
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <label className="col-form-label mt-4">Location</label>
              <input
                type="text"
                className="form-control"
                placeholder="e.g. Adelaide"
                id="inputLocation"
              ></input>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-primary">
                Save Event
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
                onClick={handleExitClick}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AltUserCalendar;
