import React from "react";
import { useStoreContext } from "../../utils/GlobalState";
import { Link } from "react-router-dom";
import PendingEvent from "../Event/PendingEvent";
import "./style.css";

function Pending() {
  const [state, dispatch] = useStoreContext();

  const EventSelected = async (event) => {
    const eventId = event.target.id;
    const selectedEvent = state.pendingEvents.filter(
      (event) => event.id === eventId
    );
    dispatch({
      type: "CURRENTBOOKING",
      currentBooking: selectedEvent[0],
    });

    if (
      state.currentBooking.bookee_id === state.user_id &&
      state.currentBooking.bookerPending
    ) {
      // im owner but not my turn
      console.log("I am bookee but it is their turn");
      await dispatch({
        type: "ACTIONREQUIRED",
        actionRequired: false,
      });
    } else if (
      state.currentBooking.bookee_id === state.user_id &&
      !state.currentBooking.bookerPending
    ) {
      // im owner and my turn
      console.log("I am bookee and it is my turn");
      await dispatch({
        type: "ACTIONREQUIRED",
        actionRequired: true,
      });
    } else if (
      state.currentBooking.booker_id === state.user_id &&
      state.currentBooking.bookerPending
    ) {
      // i booked but it is my turn
      console.log("I am booker but it is my turn");
      await dispatch({
        type: "ACTIONREQUIRED",
        actionRequired: true,
      });
    } else if (
      state.currentBooking.booker_id === state.user_id &&
      !state.currentBooking.bookerPending
    ) {
      // I booked and it is not my turn
      console.log("I am booker but it is their turn");
      await dispatch({
        type: "ACTIONREQUIRED",
        actionRequired: false,
      });
    }

    let timeLeft = 1;
    setInterval(function () {
      timeleft--;
      if (timeLeft === 0) {
        dispatch({
          type: "CLICKEDEVENT",
          clickedEvent: true,
        });
      }
    }, 100);
  };

  return (
    <div>
      <div className="container">
        <div>
          <Link to="/" className="backBtnW">
            Click me to go home
          </Link>
        </div>
        <div className="row">
          <div className="col-md-5">
            <div className="eventList">
              <div className="listContainer">
                <h2 className="h2">Pending Events</h2>
                <ul className="ul">
                  {state.pendingEvents.map((event) => {
                    return (
                      <li className="li events eventTitle" key={event.id}>
                        <Link
                          to="#"
                          scope="row"
                          id={event.id}
                          onClick={EventSelected}
                          key={event.id + "link"}
                        >
                          {event.title}
                        </Link>
                      </li>
                    );
                  })}
                </ul>
              </div>
            </div>
          </div>
          {state.clickedEvent ? (
            <PendingEvent />
          ) : (
            <div className="noEventClicked col-md-7">
              Click an event to view it!
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Pending;
