import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import API from "../../utils/API";
import { useParams } from "react-router-dom";

function AltUserCalendar() {
  const { id } = useParams();

  const [modal, setModal] = useState(false);
  const [user, setUser] = useState({});
  const [modalStyle, setModalStyle] = useState({
    class: "modal",
    style: "none",
  });
  const [dateClicked, setDateClicked] = useState("");

  useEffect(() => {
    API.getUser(id).then((res) => {
      console.log(res.data);
      setUser(res.data);
    });
  }, []);

  const handleDateClick = (arg) => {
    setModalStyle({
      class: "modal fade show",
      style: "block",
    });
    console.log(arg);

    setDateClicked(arg.dateStr);
  };

  const handleExitClick = (e) => {
    e.preventDefault();
    setModalStyle({
      class: "modal",
      style: "none",
    });
  };

  return (
    <div>
      <h1>
        Welcome to {user.first_name}'s Calendar! Cick a day to add en event!
      </h1>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      <div
        className={modalStyle.class}
        id="source-modal"
        style={{ display: modalStyle.style }}
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Add an event for {dateClicked}</h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
                onClick={handleExitClick}
              >
                <span aria-hidden="true"></span>
              </button>
            </div>
            <div className="modal-body">
              <label className="col-form-label mt-4">Default input</label>
              <input
                type="text"
                className="form-control"
                placeholder="Default input"
                id="inputDefault"
              ></input>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-primary">
                Save Event
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
                onClick={handleExitClick}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AltUserCalendar;
